mapper.update:
UPDATE tourist_attrs
SET content = #{content}, name = #{name}, category_name = #{category_name}, road_address = #{road_address}
WHERE id = #{id}

mapper.select:
SELECT id, category_name, region_id, name, content, road_address, parcel_address, lat, lng, tel, assigned_date, reg_date
FROM tourist_attrs
WHERE id = #{id};
